import { Component, ComponentFactoryResolver, OnInit, ViewChild, ViewContainerRef } from '@angular/core';
import { CreateCustomerComponent } from '../create-customer/create-customer.component';
import { CustomersComponent } from '../customers/customers.component';
import { OrdersComponent } from '../orders/orders.component';
import { ProductsComponent } from '../products/products.component';
import { CreateCustomerService } from '../services/create-customer.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  @ViewChild('contentContainer', { read: ViewContainerRef })
  contentContainer!: ViewContainerRef;

  selectedButton = 'orders';
  constructor(private componentFactoryResolver: ComponentFactoryResolver,
    private createCustomerService: CreateCustomerService) { }

  ngAfterViewInit() {
    this.renderComponent(this.selectedButton);
    this.createCustomerService.openForm$.subscribe(openForm => {
      if (openForm) {
        this.renderComponent('create-customer');
      }
    });
  }
  selectButton(componentName: string) {
    this.selectedButton = componentName;
    this.renderComponent(componentName);
  }

  renderComponent(componentName: string) {
    this.contentContainer.clear();

    let componentClass;

    switch (componentName) {
      case 'orders':
        componentClass = OrdersComponent;
        break;
      case 'products':
        componentClass = ProductsComponent;
        break;
      case 'customers':
        componentClass = CustomersComponent;
        break;
      case 'create-customer':
        componentClass = CreateCustomerComponent;
        break;
    }

    if (componentClass) {
      const componentFactory = this.componentFactoryResolver.resolveComponentFactory(componentClass);
      const componentRef = this.contentContainer.createComponent(componentFactory);
    }
  }
}
<div class="content">
  <div class="buttons">
    <button class="nav-btn" (click)="selectButton('orders')" [ngClass]="{ 'selected': selectedButton === 'orders' }">Orders</button>
    <button class="nav-btn" (click)="selectButton('products')" [ngClass]="{ 'selected': selectedButton === 'products' }">Products</button>
    <button class="nav-btn" (click)="selectButton('customers')" [ngClass]="{ 'selected': selectedButton === 'customers' }">Customers</button>
  </div>
  <div #contentContainer></div>
</div>
