import { Component, Inject, OnInit } from '@angular/core';
import { ICustomer } from '../models/customer';
import { CustomersService } from '../services/customers.service';

@Component({
  selector: 'customers',
  templateUrl: './customers.component.html'
})
export class CustomersComponent implements OnInit {
  public customers: ICustomer[] = [];

  constructor(private customersService: CustomersService, private createCustomerService: CreateCustomerService) { }


  ngOnInit(): void {
    this.customersService.getAll().subscribe(customers => {
      console.log(customers);
      this.customers = customers;
    })
  }

  openCustomerForm() {
    this.createCustomerService.setOpenForm(true);
  }

}
interface Customer {
  id: number,
  customerName: string,
  address: string,
  totalOrderedCost: number,
  ordersCount: number
}
