<button (click)="selectButton('orders')" [ngClass]="{ 'selected': selectedButton === 'orders' }">Orders</button>
<button (click)="selectButton('products')" [ngClass]="{ 'selected': selectedButton === 'products' }">Products</button>
<button (click)="selectButton('customers')" [ngClass]="{ 'selected': selectedButton === 'customers' }">Customers</button>
<div #contentContainer></div>
