import { Component, Input, OnInit } from "@angular/core";
import { FormControl, FormGroup, Validators } from "@angular/forms";
import { ICreateOrder } from "../models/createOrder";
import { ICustomer } from "../models/customer";
import { IProduct } from "../models/product";
import { ISelectListCustomer } from "../models/selectListCustomer";
import { IStatus } from "../models/status";
import { CreateOrderService } from "../services/create-order.service";

@Component({
  selector: 'create-order',
  templateUrl: './create-order.component.html',
  styleUrls: ['./create-order.component.css']
})
export class CreateOrderComponent implements OnInit {
  currentDate: string | undefined;
  selectCustomers: ISelectListCustomer[] = [];
  selectStatuses: any[] = [];
  selectedValue: any;
  selectedStatusValue: any;
  @Input() newOrderNumber!: number;
  defaultCustomer!: ICustomer;
  defaultStatus!: IStatus;
  products: IProduct[] = []
  totalCost!: number;
  formOrder = new FormGroup({
    OrderNumber: new FormControl<number>(this.newOrderNumber),
    Status: new FormControl<IStatus>(this.defaultStatus, [
      Validators.required
    ]),
    Comment: new FormControl<string>(''),
    Customer: new FormControl<ICustomer>(this.defaultCustomer, [
      Validators.required
    ]),
    OrderedProducts: new FormControl<IProduct[]>(this.products)
  })
  formData: ICreateOrder = {
    OrderNumber: this.newOrderNumber,
    Status: this.defaultStatus,
    Comment: '',
    Customer: this.defaultCustomer,
    OrderedProducts: this.products
  }
  constructor(private createOrderService: CreateOrderService) { }
  ngOnInit(): void {
    this.createOrderService.getSelectListCustomers().subscribe(data => {
      this.selectCustomers = data;
    });

    this.createOrderService.getSelectListStatuses().subscribe(data => {
      this.selectStatuses = Object.entries(data).map(([id, title]) => ({ id: +id, title }));
    });

    const today = new Date();
    this.currentDate = today.toISOString().substring(0, 10);

    this.totalCost = 0;
    this.getTotalCost()
  }
  getTotalCost() {
    for (let i = 0; i < this.products.length; i++) {
      this.totalCost += this.products[i].price
    }
  }
  saveOrder() {
    console.log(this.formOrder);
    if (this.formOrder.valid) {
      this.formData = {
        OrderNumber: this.newOrderNumber,
        Status: this.formOrder.value.Status ?? this.defaultStatus,
        Comment: this.formOrder.value.Comment ?? '',
        Customer: this.formOrder.value.Customer ?? this.defaultCustomer,
        OrderedProducts: this.products
      };
    }
    console.log(this.formData)
  }
  backToOrders() {
    this.createOrderService.triggerBackToOrders()
  }
  get customer() {
    return this.formOrder.controls.Customer as FormControl;
  }
  get status() {
    return this.formOrder.controls.Status as FormControl;
  }
}
<div class="createOrderContent">
  <h1>New order</h1>
  <form [formGroup]="formOrder" (ngSubmit)="saveOrder()" class="createCustomerFrom">
    <div class="order-info">
      <div class="order-info-block me-5">
        <label class="me-5">Order Number:</label>
        <p>{{newOrderNumber}}</p>
      </div>
      <div class="order-info-block">
        <label class="me-5">Order Date:</label>
        <p class="me-5">{{currentDate}}</p>
      </div>
    </div>
    <label class="me-5">Customer:</label>
    <select class="order-select" [(ngModel)]="selectedValue" formControlName="Customer">
      <option *ngFor="let item of selectCustomers" [value]="item.id">{{ item.customerName }}</option>
    </select>
    <br />
    <label class="me-5">Status:</label>
    <select class="order-select" [(ngModel)]="selectedStatusValue" formControlName="Status">
      <option  *ngFor="let item of selectStatuses" [value]="item.id">{{ item.title }}</option>
    </select>
    <br />
    <label class="me-5">Total Cost:</label>
    <p>{{totalCost | currency}}</p>
    <div>
      <div class="products-nav">
        <h3>Add products:</h3>
        <button>Add Products To Order</button>
      </div>
      <table class='table table-striped' aria-labelledby="tableLabel">
        <tr>
          <th>
            <div class="table-header-block">
              Product Id
            </div>
          </th>
          <th>
            <div class="table-header-block">
              Product Name
            </div>
          </th>
          <th>
            <div class="table-header-block">
              Product Category
            </div>
          </th>
          <th>
            <div class="table-header-block">
              Product Size
            </div>
          </th>
          <th>
            <div class="table-header-block">
              Quantity
            </div>
          </th>
          <th>
            <div class="table-header-block">
              Price
            </div>
          </th>
        </tr>
        <!--here must be logic of adding products-->
        <tr *ngFor="let product of products; let i = index">
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">
              <a href="#"></a>
            </div>
          </td>
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">

            </div>
          </td>
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">

            </div>
          </td>
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">

            </div>
          </td>
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">

            </div>
          </td>
          <td>
            <div class="table-content-block" [ngClass]="{ 'white-row': i % 2 === 0, 'blue-row': i % 2 !== 0 }">

            </div> 
          </td>
        </tr>
      </table>
    </div>
    <label>Comment: </label>
    <textarea formControlName="Comment"></textarea>
    <br />
    <p class="error" *ngIf="customer.errors?.required && customer.touched">Customer is required.</p>
    <p class="error" *ngIf="status.errors?.required && status.touched">Status is required.</p>
    <button type="submit">Save</button>
    <button (click)="backToOrders()">Cancel</button>
  </form>
</div>
