import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  //isExpanded = false;

  //collapse() {
  //  this.isExpanded = false;
  //}

  //toggle() {
  //  this.isExpanded = !this.isExpanded;
  //}
  getOrdersList() {
    console.log("orders")
  }
  getProductsList() {
    console.log("products")
  }
  getCustomersList() {
    console.log("customers")
  }
}
<div class="menu">
  <nav-button label="Orders" (onClick)="getOrdersList()" [selected]="selectedButton === 1"></nav-button>
  <nav-button label="Products" (onClick)="getProductsList()" [selected]="selectedButton === 2"></nav-button>
  <nav-button label="Customers" (onClick)="getCustomersList()" [selected]="selectedButton === 3"></nav-button>
</div>



