import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { ICreateCustomer } from '../models/CreateCustomer';

@Injectable({
  providedIn: 'root'
})
export class CreateCustomerService {
  private _openForm = new BehaviorSubject<boolean>(false);
  openForm$ = this._openForm.asObservable();
  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) { }

  setOpenForm(value: boolean) {
    this._openForm.next(value);
  }
  createCustomer(customerData: ICreateCustomer): Observable<ICreateCustomer> {
    return this.http.post<ICreateCustomer>(this.baseUrl + 'customer', customerData);
  }
}
