import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { ApiService } from './service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';
  newdata: any;
  public customers: Customers[] = [];
/*  constructor(private _apiService: ApiService) { }*/

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<Customers[]>(baseUrl + 'customers').subscribe(result => {
      this.customers = result;
    }, error => console.error(error));
  }

  //ngOnInit() {
  //  this._apiService.getData().subscribe(res => {
  //    this.newdata = res;
  //    console.log(res);
  //  })
  //}
}
interface Customers {
  id: number,
  customerName: string,
  address: string,
  totalOrderedCost: number,
  ordersCount: number
}
<body>
  <main class="container">
    <!--<router-outlet></router-outlet>-->
    <table>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Address</th>
        <th>TotalOrderedCost</th>
        <th>OrdersCount</th>
      </tr>
      <tr>
        <td>{{newdata.id}}</td>
        <td>{{newdata.customerName}}</td>
        <td>{{newdata.address}}</td>
        <td>{{newdata.totalOrderedCost}}</td>
        <td>{{newdata.ordersCount}}</td>
      </tr>
    </table>
  </main>
</body>
